(function() { tinymce.PluginManager.requireLangPack('embedvideo'); tinymce.create('tinymce.plugins.EmbedVideo', { init: function(ed, url) { ed.addCommand('mceEmbedVideo', function() { ed.windowManager.open({ file: url + '/dialog.htm', width: 480 + parseInt(ed.getLang('embedvideo.delta_width', 0)), height: 320 + parseInt(ed.getLang('embedvideo.delta_height', 0)), inline: 1 }, { plugin_url: url, some_custom_arg: 'custom arg' }) }); ed.addButton('embedvideo', { title: 'embedvideo.desc', cmd: 'mceEmbedVideo', image: url + '/img/embed.jpg' }); ed.onNodeChange.add(function(ed, cm, n) { cm.setActive('embedvideo', n.nodeName == 'IMG') }) }, createControl: function(n, cm) { return null }, getInfo: function() { return { longname: 'Embed Video plugin', author: 'agent 007', authorurl: 'eldar', infourl: '', version: "1.0"} } }); tinymce.PluginManager.add('embedvideo', tinymce.plugins.EmbedVideo) })();